                self.frame_3.setHidden(False)
                self.descriptionMinimizeButton.setHidden(False)
                self.descriptionBox.setText(self.selectedItem["INFO"])
                self.descriptionImportanceBar.setValue(self.selectedItem["IMPORTANCE"])
                
